$("body").append($("<input>").attr("placeholder","Enter Task")).append($("<button>").text("Add Task").click(t=>{$(".taskList").append($("<li>").text($("input").val()).append($("<button>").text("↑").addClass("up").click(t=>{$(t.target).parent().insertBefore($(t.target).parent().prev())})).append($("<button>").text("↓").addClass("down").click(t=>{$(t.target).parent().insertAfter($(t.target).parent().next())})).append($("<button>").text("❌").addClass("delete").click(t=>{$(t.target).parent().remove()})))})).append($("<ul>").addClass("taskList"));